		},
		goHome() {
			uni.reLaunch({
				url: '/'+HOME_PATH,
			})
		},
		// #endif
		onClickLeft() {
			if (this.clickLeftBack) {
				uni.navigateBack()
			} else {
				this.$emit("clickLeft");
			}
		},
		onClickRight() {
